// import { HttpClient } from '@angular/common/http';
import { Component, OnInit} from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { SignupLoginService } from '../signup-login.service';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent implements OnInit{
  //[x: string]: any;
 
   signupForm!: FormGroup;
  constructor(private formBuilder: FormBuilder, private service: SignupLoginService, private router: Router) {}

  ngOnInit(): void {
    this.signupForm = this['formBuilder'].group({
      firstName: new FormControl('', Validators.required),
      lastName: new FormControl('', Validators.required),
      username: new FormControl('', Validators.required),
      password: new FormControl('', Validators.required),
      confirmPassword: new FormControl('', Validators.required)
    })
  }

  get firstName(){
    return this.signupForm.get('firstName');
  }
  get lastName(){
    return this.signupForm.get('lastName');
  }
  get username(){
    return this.signupForm.get('username');
  }
  get password(){
    return this.signupForm.get('password');
  }
  get confirmPassword(){
    return this.signupForm.get('confirmPassword');
  }


  save(){
  this.service.save(this.signupForm).subscribe(
      results => {
        console.log(results);
        if(results){
          //navigate to dashboard
          this.router.navigate(['login']);
        }
      }
   )
  }
}



<div class="container2">
<form [formGroup] = "signupForm" (ngSubmit)="save()" style="border:1px solid #ccc">
    <div class="content">
    <h1>Sign Up</h1>
    <p>Please fill in this form to create an account.</p>
    <hr>
    <!-- <div class="row g-3 align-items-center"> -->
        <!-- <div class="col-auto"> -->
            <div>
            <label for="firstName"><b>First Name:</b></label>
   <input type="text" formControlName="firstName" placeholder="First Name"/> <br>
    <span *ngIf="firstName.touched && firstName.invalid">
        First Name is required one
    </span>
    <br/>
    </div>
    <!-- </div> -->
<!-- </div> -->
    <!-- <div class="row g-3 align-items-center"> -->
        <!-- <div class="col-auto"> -->
            <div>
            <label for="lastName"><b> Last Name:</b> </label>
   <input type="text" formControlName="lastName" placeholder="Last Name"/> <br>
    <span *ngIf="lastName.touched && lastName.invalid">
        Last Name is required one
    </span>
    <br/>
   </div>
    <!-- </div> -->
    <!-- </div> -->
    <!-- <div class="row g-3 align-items-center"> -->
        <!-- <div class="col-auto"> -->
            <div>
            <label for="username"><b>Username:</b>  </label>
    <input type="email" formControlName="username" placeholder="Username"/> <br>
    <span *ngIf="username.touched && username.invalid">
        Username is required one
    </span>
    <br/>
    </div>
    <!-- </div> -->
    <!-- </div> -->
    <!-- <div class="row g-3 align-items-center"> -->
        <!-- <div class="col-auto"> -->
            <div>
            <label for="password"><b>Password:</b></label>
    <input type="password" formControlName="password" placeholder="Password"/> <br>
     <span *ngIf="password.touched && password.invalid">
       Password is required one
    </span>
    <br/>
    </div>
    <!-- </div> -->
    <!-- </div> -->
   
    <!-- <div class="row g-3 align-items-center"> -->
        <!-- <div class="col-auto"> -->
            <div>                                               
            <label for="confirmpassword"><b>Confirm Password:</b></label>
    <input type="password" formControlName="confirmPassword" placeholder="Confirm Password"/> <br>
     <span *ngIf="confirmPassword.touched && password.value != confirmPassword.value">
    Password and Confirm Password should be same
    </span>
    <br/>
    </div>
    <!-- </div> -->
    <!-- </div> -->
 <br/>
    <label>
        <input type="checkbox" checked="checked" name="remember" style="margin-bottom:15px"> Remember me
      </label>
      
      <p>By creating an account you agree to our <a href="#" style="color:dodgerblue">Terms & Privacy</a>.</p>
  
    <!-- <button class="btn btn-primary">
    <input type="submit" class="btn btn-primary" value="Registed"/>
  </button> -->
  <!-- <button type="button" class="btn btn-primary">Cancel</button> -->
  <div class="clearfix">
    <button type="button" class="cancelbtn">Cancel</button>
    <button type="submit" class="signupbtn">Sign Up</button>
  </div>
</div>
</form>
</div>
